import React, { useState } from "react";

// import Story from "../../components/Story";

import "./Stories.scss";
import Story from "../../components/Story"

const Stories = ({ stories, getUserHandler }) => {

  const [showStory, toggleShowStory] = useState(false)

  let handleClick = event => {
    toggleShowStory(!showStory);
  }



  return (
    <React.Fragment>
      <section className="stories" data-testid="stories">
        <div className="container">
          <button className="user__thumb user__thumb--hasNew" onClick={handleClick}>
            <div className="user__thumb__wrapper">
              <img
                src="https://viniciusvinna.netlify.app/assets//api-instagram/profiles/black-panther/black-panther-profile.jpg"
                alt="black panther"
              />
            </div>
          </button>
          <button className="user__thumb">
            <div className="user__thumb__wrapper">
              <img
                src="https://viniciusvinna.netlify.app/assets//api-instagram/profiles/black-panther/black-panther-profile.jpg"
                alt="black panther"
              />
            </div>
          </button>
        </div>
      </section>

      {showStory && (
        <Story 
          story={{
            "videoUrl": "https://viniciusvinna.netlify.app/assets/api-instagram/profiles/black-panther/black-panther-stories.mp4"
          }} user="blackPanther" handleClose={ () => toggleShowStory(!showStory)} />
      )}
    </React.Fragment>
  );
};

export default Stories;
