import React, { useState } from "react";

// import Story from "../../components/Story";

import "./Stories.scss";
import Story from "../../components/Story"

const Stories = ({ stories, getUserHandler }) => {

  const [showStory, toggleShowStory] = useState(false)
  const [selectedStory, setSelectedHistory] = useState({});
  const [selectedProfile, setSelectedProfile] = useState({});

  const findStoryById = id => stories.find(story => story.id === id);

  let handleClick = story => {

    const foundStory = findStoryById(story.id);
    const profileData = getUserHandler(story.userId);

    setSelectedProfile(profileData);
    setSelectedHistory(foundStory);
    toggleShowStory(!showStory);
  }



  return (
    <React.Fragment>
      <section className="stories" data-testid="stories">
        <div className="container">
          {stories.map((story, index) => {
            const profileData = getUserHandler(story.userId);
            return(
              <button key={story.id} onClick={() => handleClick(story)} className={`user__thumb ${index == 0 && 'user__thumb--hasNew'}`}>
                <div className="user__thumb__wrapper">
                  <img src={profileData.avatar} alt={profileData.name} />
                </div>
              </button>
            )
          })}
        </div>
      </section>

      {showStory && (
        <Story 
          story={{
            "videoUrl": "https://viniciusvinna.netlify.app/assets/api-instagram/profiles/black-panther/black-panther-stories.mp4"
          }} user="blackPanther" handleClose={ () => toggleShowStory(!showStory)} />
      )}
    </React.Fragment>
  );
};

export default Stories;
