import React, { useState } from "react";
import { Link } from "react-router-dom";

import "./Post.scss";

const Post = ({ postInfo, userInfo }) => {
  const [follow, setFollow] = useState(false);
  const [like, setLike] = useState(false);
  const toggleFollow = (isFollowing) => setFollow(isFollowing);

  return (
    <article className="post" data-testid="post">
      <header className="post__header">
        <div className="user">
          <Link to="/user/black-panther" className="user__thumb">
            <img
              src="https://viniciusvinna.netlify.app/assets//api-instagram/profiles/black-panther/black-panther-profile.jpg"
              alt=""
            />
          </Link>
          <Link to="/user/black-panther" className="user__name">
            T'Challa
          </Link>
        </div>
        <button className="post__context" onClick={() => toggleFollow(!follow)}>
          {follow ? (
            <span className="follow-btn is-following">{"Seguindo"}</span>
          ) : (
            <span className="follow-btn">{"Seguir"}</span>
          )}
        </button>
      </header>
      <figure className="post__figure">
        <img
          src="https://viniciusvinna.netlify.app/assets//api-instagram/profiles/black-panther/black-panther-profile.jpg"
          alt=""
        />
      </figure>
      <nav className="post__controls">
        <button className="post__control" onClick={() => setLike(!like)}>
          {like ? (
            <i className="fas fa-heart" />
          ) : (
            <i className="far fa-heart" />
          )}
        </button>
        <div className="post__status">
          <div className="user">
            <span>
              curtido por <Link to="/user/black-panther">Black Panther</Link> e
              outras 5 pessoas
            </span>
          </div>
        </div>
      </nav>
    </article>
  );
};

export default Post;
