import React, { useState } from "react";

import SuccessMessage from "../../components/SuccessMessage";

import "./UserForm.scss";

const UserForm = () => {
  const [name, setName] = useState("name");
  const [avatar, setAvatar] = useState("");
  const [username, setUsername] = useState("username");
  const [email, setEmail] = useState("email");
  const [submit, setSubmit] = useState(false);

  const handleSetName = (event) => {
    const { value } = event.target;
    setName(value);
  };

  const handleSetAvatar = (event) => {
    const { value } = event.target;
    setAvatar(value);
  };

  const handleSetUsername = (event) => {
    const { value } = event.target;
    setUsername(value);
  };

  const handleSetEmail = (event) => {
    const { value } = event.target;
    setEmail(value);
  };

  const handleAddUser = (event) => {
    setSubmit(true);
  };

  return (
    <React.Fragment>
      <section className="user" data-testid="user-form">
        <div className="container">
          <div className="profile-data">
            <div className="user">
              <div className="user__thumb">
                {avatar ? (
                  <img src={avatar} />
                ) : (
                  <img src="https://viniciusvinna.netlify.app/assets/api-instagram/profiles/profile-placeholder.png" />
                )}
              </div>
              {name && (
                <p className="user__name">
                  {name}
                  <span>@{username}</span>
                </p>
              )}
            </div>
          </div>
        </div>
      </section>
      <section className="post__form">
        <div className="container">
          <div className="post__form__wrapper">
            <label>Nome</label>
            <input
              type="text"
              value={name}
              placeholder="Ex. Fulano da Silva"
              onChange={(event) => handleSetName(event)}
            />
            <label>Usu√°rio</label>
            <input
              type="text"
              value={username}
              placeholder="Ex. fulano.da.silva"
              onChange={(event) => handleSetUsername(event)}
            />
            <label>E-mail</label>
            <input
              type="text"
              value={email}
              placeholder="Ex. fulano@email.com"
              onChange={(event) => handleSetEmail(event)}
            />
            <label>
              Url da Imagem de Perfil (use a url da imagem do Linkedin)
            </label>
            <input
              type="text"
              value={avatar}
              placeholder="Http://...."
              onChange={(event) => handleSetAvatar(event)}
            />
            <button type="button" onClick={(event) => handleAddUser(event)}>
              Cadastrar{" "}
            </button>
          </div>
        </div>
      </section>

      {submit && <SuccessMessage />}
    </React.Fragment>
  );
};

export default UserForm;
